import React, { useState } from "react";
import "./Compteurs.css";

const Compteurs = () => {
  const [c1, setC1] = useState(0);
  const [c2, setC2] = useState(0);
  const [c3, setC3] = useState(0);
  return (
    <div className="bgdCompteurs">
      <div className="compteur">
        <button
          onClick={() => {
            setC1(c1 - 1);
          }}
        >
          -
        </button>
        <span>{c1}</span>
        <button
          onClick={() => {
            setC1(c1 + 1);
          }}
        >
          +
        </button>
      </div>
      <div className="compteur">
        <button
          onClick={() => {
            setC1(c2 - 1);
          }}
        >
          -
        </button>
        <span>{c2}</span>
        <button
          onClick={() => {
            setC1(c2 + 1);
          }}
        >
          +
        </button>
      </div>
      <div className="compteur">
        <button
          onClick={() => {
            setC1(c3 - 1);
          }}
        >
          -
        </button>
        <span>{c3}</span>
        <button
          onClick={() => {
            setC1(c3 + 1);
          }}
        >
          +
        </button>
      </div>
    </div>
  );
};

export default Compteurs;
